import React, { useEffect, useReducer } from 'react';

import API, { graphqlOperation } from '@aws-amplify/api';
import Amplify, { PubSub } from '@aws-amplify';
import { AWSIoTProvider } from '@aws-amplify/pubsub/lib/Providers';


import TextLoop from "react-text-loop";
import cxs from "cxs/component";


import AWS_logo from "./images/AWS_logo_RGB_WHT.png"

import awsconfig from './aws-exports';
import './App.css';

// IoT subscription
Amplify.addPluggable(new AWSIoTProvider({
  aws_pubsub_region: process.env.REACT_APP_REGION,
  aws_pubsub_endpoint: `wss://${process.env.REACT_APP_MQTT_ID}.iot.${process.env.REACT_APP_REGION}.amazonaws.com/mqtt`,
}));

Amplify.PubSub.subscribe('real-time-weather').subscribe({
  next: data => console.log('Message received', data),
  error: error => console.error(error),
  close: () => console.log('Done'),
});

const StyledTextLoop = cxs(TextLoop)({
  display: "block"
});


class Greeting extends React.Component {
  state = {
    hour: null,
    username: 'Alyssa'
  }

  componentDidMount() {
    this.getHour()
  }
    getHour = () => {
   const date = new Date();
   const hour = date.getHours()
   this.setState({
      hour
   });
  }
  render(){
    const {hour, username} = this.state;
    return (
      <div className='App'>
        <h2>{hour < 12 ? `Good Morning ${username}` : `Good evening ${username}`}</h2>
      </div>
    );
  }
}


function App() {

  return (
    <div className="App"style = {{height:"100vh", justifyContent: 'center'}}>
      {/*<button onClick={createNewTodo}>Add Todo</button>*/}

      <div className="Message">
      <TextLoop interval='4000' fade='true' children={["How can I help?", "	¿Cómo puedo ayudarte?",<img src={AWS_logo} alt="Logo" />]} />
       </div>
    </div>
  );
}

export default App;
