import React, { useEffect, useReducer } from 'react';

import API, { graphqlOperation } from '@aws-amplify/api';
import PubSub from '@aws-amplify/pubsub';

import TextLoop from "react-text-loop";
import cxs from "cxs/component";


import AWS_logo from "./AWS_logo_RGB_WHT.png"

import awsconfig from './aws-exports';
import './App.css';

API.configure(awsconfig);
PubSub.configure(awsconfig);

// Action Types
const QUERY = 'QUERY';
const SUBSCRIPTION = 'SUBSCRIPTION';

const initialState = {
  todos: [],
};

const StyledTextLoop = cxs(TextLoop)({
  display: "block"
});


const reducer = (state, action) => {
  switch (action.type) {
    case QUERY:
      return {...state, todos: action.todos};
    case SUBSCRIPTION:
      return {...state, todos:[...state.todos, action.todo]}
    default:
      return state;
  }
};



function App() {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <div className="App"style = {{height:"100vh", justifyContent: 'center', textAlign: 'center', top: '50%'}}>
      {/*<button onClick={createNewTodo}>Add Todo</button>*/}

      <img src={AWS_logo} alt="Logo" />
      <div className="Message">
      <TextLoop children={["How can I help", "	¿Cómo puedo ayudarte", "我该如何帮助"]} />?
       </div>
    </div>
  );
}

export default App;
